mapper.select:
SELECT rnum, bno, title, writerid, replycnt, viewcnt, to_char(regdate, 'YYYY-MM-DD HH24:MI') regdate, recommendcnt FROM ( SELECT ROWNUM AS rnum, a.* FROM ( SELECT * FROM bbs_board WHERE del_yn='n'
AND title like '%'||#{keyword}||'%' AND writerId like '%'||#{keyword}||'%' AND content like '%'||#{keyword}||'%'
order by bno desc ) a ) WHERE rnum between #{startRow} and #{endRow}

mapper.update:
UPDATE  bbs_board
SET  title =  #{title}, content = #{content}, writerId = #{writerId}, viewcnt = #{viewcnt}
WHERE  bno  = #{bno}

UPDATE  bbs_board
SET     viewcnt = viewcnt+1
WHERE  bno  = #{bno}

UPDATE  bbs_board
SET     recommendcnt=recommendcnt+1
WHERE   bno  = #{bno}

UPDATE  bbs_board
SET     replycnt = replycnt + 1
WHERE   bno  = #{bno}

UPDATE bbs_reply
  SET content = #{content}, writerId = #{writerId}, regDate = #{regDate}
WHERE rno = #{rno}

UPDATE bbs_reply
  SET  recommendcnt = recommendcnt + 1
 WHERE rno = #{rno}

mapper.select:
SELECT fno, bno, ofilename, sfilename, filesize
FROM bbs_file
WHERE bno = #{bno}

SELECT ofilename
FROM bbs_file
WHERE fno=#{fno}

SELECT COALESCE(MAX(bno), 0) AS bno
FROM bbs_boardlike
WHERE userId = #{userId} AND bno = #{bno}

SELECT COALESCE(MAX(rno), 0) AS rno
FROM bbs_replylike
WHERE userId = #{userId} AND rno = #{rno}

SELECT COALESCE(MAX(bno), 0) AS bno
FROM bbs_boardview
WHERE userId= #{userId} AND bno = #{bno}
